package com.ElementeryProgramming.Chapter2;

public class ComputeAreaExamp1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Step 1: Read in radius
		// Step 2: Compute area
		// Step 3: Display the area

		double radius=11;
		double area;
		//The program declares radius and area as variables
		//The reserved word double indicates that radius and area are floating-point values stored in the computer
		area=3.14*radius*radius;
		System.out.println("The area of the Circle is: " +area);
	}

}
